from typing import List
import heapq


class Solution:
    def highestPeak(self, isWater: List[List[int]]) -> List[List[int]]:
        h = []
        M, N = len(isWater), len(isWater[0])
        mat = [[None] * N for _ in range(M)]
        q=[]
        for x in range(M):
            for y in range(N):
                if isWater[x][y] == 1:
                    h.append((0, x, y))
                    mat[x][y]=0
                    q.append((x,y))
        height=0
        while q:
            height+=1
            new_q=[]
            for x,y in q:
                for dx,dy in ((-1,0),(1,0),(0,1),(0,-1)):
                    x1=x+dx
                    y1=y+dy
                    if 0<=x1<M and 0<=y1<N and mat[x1][y1]==None:
                        mat[x1][y1]=height
                        new_q.append((x1,y1))
            q=new_q
        return mat



