from typing import List
class Solution:
    def maximumScore(self, nums: List[int], multipliers: List[int]) -> int:
        N = len(nums)
        M = len(multipliers)
        dp = [[0] * (M + 1) for _ in range(M + 1)]
        for m in range(1, M + 1):
            for left in range(m + 1):
                right = m - left

                dp[left][right] = max(
                    dp[left - 1][right] + multipliers[m - 1] * nums[left - 1] if left - 1 >= 0 else -float("inf"),
                    dp[left][right - 1] + multipliers[m - 1] * nums[N - 1 - (right - 1)] if right - 1 >= 0 else -float(
                        "inf")
                    )
        return max(dp[left][M - left] for left in range(M + 1))


