class Solution(object):

    def numDistinctIslands2(self, grid):
        """
        :type grid: List[List[int]]
        :rtype: int
        """
        from collections import deque
        if grid == [] or grid[0] == []:
            return 0
        m, n = len(grid), len(grid[0])
        self.islands = set()
        self.visited = set()

        def bfs(x, y):
            q = deque([(x, y)])
            res = set([(x, y)])
            self.visited.add((x, y))
            while q:
                x0, y0 = q.popleft()
                for dx, dy in ((0, 1), (0, -1), (1, 0), (-1, 0)):
                    x1, y1 = x0 + dx, y0 + dy
                    if 0 <= x1 < m and 0 <= y1 < n and grid[x1][y1] == 1 and (x1, y1) not in self.visited:
                        q.append((x1, y1))
                        self.visited.add((x1, y1))
                        res.add((x1, y1))
            return res

        def alignMostTopLeft(arr):
            min_x = min([p[0] for p in arr])
            min_y = min([p[1] for p in arr])
            arr = tuple(sorted([(a - min_x, b - min_y) for a , b in arr]))
            return arr

        def rotate90Degree(arr):
            arr = [(y, m - x - 1) for x, y in arr]
            arr = alignMostTopLeft(arr)
            return arr

        def rotate180Degree(arr):
            arr = [(m - x - 1, n - y - 1) for x, y in arr]
            arr = alignMostTopLeft(arr)
            return arr

        def rotate270Degree(arr):
            arr = [(n - y - 1, x) for x, y in arr]
            arr = alignMostTopLeft(arr)
            return arr

        def reflectionLeftRight(arr):
            arr = [(x, n - y - 1) for x, y in arr]
            arr = alignMostTopLeft(arr)
            return arr

        def reflectionUpDown(arr):
            arr = [(m - x - 1, y) for x, y in arr]
            arr = alignMostTopLeft(arr)
            return arr

        ans = 0
        for x in xrange(m):
            for y in xrange(n):
                if grid[x][y] == 1 and (x, y) not in self.visited:
                    arr = bfs(x, y)
                    arr = alignMostTopLeft(arr)
                    flag = True
                    temp_set = set([arr, reflectionLeftRight(arr), reflectionUpDown(arr), rotate90Degree(arr), rotate180Degree(arr), rotate270Degree(arr)])
                    for temp in temp_set:
                        if temp in self.islands:
                            flag = False
                    if flag:
                        ans += 1
                    self.islands |= temp_set
        return ans


